(2519, 'en', 'default', '16fd34c3de2b02f60c7813d5965faecb', '<strong>WordPress</strong> &mdash; Updating your WordPress installation is a simple one-click procedure: just <strong>click on the &#8220;Update Now&#8221; button</strong> when you are notified that a new version is available.', NULL, NULL, 'LINE', NULL, 0, '', 'a2c5ffc4b69c8d049bce519b8013c8cb'),
(2520, 'en', 'default', '72247007ca8dd9d5b747cd2d13855ae4', 'In most cases, WordPress will automatically apply maintenance and security updates in the background for you.', NULL, NULL, 'LINE', NULL, 0, '', 'f608b0ae3b2099cd13950879df8872a3'),
