(2358, 'en', 'default', '0bdd62cf14b1ac2df851e97eed7faa8c', '<strong>Edit</strong> takes you to the editing screen for that post. You can also reach that screen by clicking on the post title.', NULL, NULL, 'LINE', NULL, 2, '', '74deb9541548900791736df7e47727d1'),
(2359, 'en', 'default', '099f3df0632488f2c47e2658d95e0e76', '<strong>Quick Edit</strong> provides inline access to the metadata of your post, allowing you to update post details without leaving this screen.', NULL, NULL, 'LINE', NULL, 2, '', 'aec7db39f9755f7c2dc6e2b37c8412c2'),
(2360, 'en', 'default', '1ac12183049e2cd887bb8ee6b488d5fb', '<strong>Trash</strong> removes your post from this list and places it in the trash, from which you can permanently delete it.', NULL, NULL, 'LINE', NULL, 2, '', 'fb4aa500e080edf20572554e14786e36'),
(2361, 'en', 'default', 'b807523847b38ac8553394a6e915ef62', '<strong>Preview</strong> will show you what your draft post will look like if you publish it. View will take you to your live site to view the post. Which link is available depends on your post&#8217;s status.', NULL, NULL, 'LINE', NULL, 2, '', 'feb9ddd4b67850aee6a2151805f1dfa8'),
(2362, 'en', 'default', 'c83a2faddec053e93996bea49df84dd8', 'You can also edit or move multiple posts to the trash at once. Select the posts you want to act on using the checkboxes, then select the action you want to take from the Bulk Actions menu and click Apply.', NULL, NULL, 'LINE', NULL, 2, '', '95e6f8b591af95b7e141df1318db45fb'),
