(2516, 'en', 'default', 'e45bfeb9fa95a202319d55f3ba06991b', 'On this screen, you can update to the latest version of WordPress, as well as update your themes, plugins, and translations from the WordPress.org repositories.', NULL, NULL, 'LINE', NULL, 2, '', 'e372d88371c5bbedffe3b8b4d1dc9d54'),
(2517, 'en', 'default', '413ea0a127a3a100b83ec95eacabc6c3', 'If an update is available, you&#8127;ll see a notification appear in the Toolbar and navigation menu.', NULL, NULL, 'LINE', NULL, 2, '', '27b11d9c8aed915251b6af3836623b77'),
(2518, 'en', 'default', 'f30629be5fa2dd68e817486c218122de', 'Keeping your site updated is important for security. It also makes the internet a safer place for you and your readers.', NULL, NULL, 'LINE', NULL, 2, '', 'd0eaf747d8cb15ad1a168eb6ba6e207c'),
(2519, 'en', 'default', '16fd34c3de2b02f60c7813d5965faecb', '<strong>WordPress</strong> &mdash; Updating your WordPress installation is a simple one-click procedure: just <strong>click on the &#8220;Update Now&#8221; button</strong> when you are notified that a new version is available.', NULL, NULL, 'LINE', NULL, 2, '', 'a2c5ffc4b69c8d049bce519b8013c8cb'),
(2520, 'en', 'default', '72247007ca8dd9d5b747cd2d13855ae4', 'In most cases, WordPress will automatically apply maintenance and security updates in the background for you.', NULL, NULL, 'LINE', NULL, 2, '', 'f608b0ae3b2099cd13950879df8872a3'),
