(6324, 'en', 'default', '67ebe7a24df35d5ec7ed817c5842a194', 'You are about to remove the following plugin:', NULL, NULL, 'LINE', NULL, 2, '', 'ea9e1c9dd74972fc87e537c0b8a75783'),
(6325, 'en', 'default', 'bbad7ae088e6c0da4561965a1c369358', 'You are about to remove the following plugins:', NULL, NULL, 'LINE', NULL, 2, '', '5c3fe5012767405bbfe2446333b20717'),
(6326, 'en', 'default', '5e2e56330bcbe7fad88b2f4f103f7ddb', 'You are currently editing the page that shows your latest posts.', NULL, NULL, 'LINE', NULL, 2, '', '8e7076e0d8e43c940232b9e797f28ddf'),
(6327, 'en', 'default', '96cef5b3d6d082373acf8e547cf63ed4', 'You are now logged out everywhere else.', NULL, NULL, 'LINE', NULL, 2, '', 'eaa09b45dbf6e5106e26f1575642c8a7'),
(6328, 'en', 'default', 'f6435aa05732baca22a6de8167d3bfd8', 'You are only logged in at this location.', NULL, NULL, 'LINE', NULL, 2, '', '6efa0e36e3fa8fa35878b98eefa724f7'),
(6329, 'en', 'default', 'a2ffc66c941814a9d5b0bb75664a1438', 'You are using a development version (%1$s). Cool! Please <a href="%2$s">stay updated</a>.', NULL, NULL, 'LINE', NULL, 2, '', '1611f33e667d8986d6e5af86e2632027'),
(6330, 'en', 'default', 'ead95faf097c050ce8272235ab6d14c4', 'You are using a development version of WordPress. These feature plugins are also under development. <a href="%s">Learn more</a>.', NULL, NULL, 'LINE', NULL, 2, '', 'c6341544fa2046eec14107fc5e0c2991'),
(6331, 'en', 'default', 'cd695c6558c271b3503ba79fa724359b', 'You are using a development version of WordPress. You can update to the latest nightly build automatically:', NULL, NULL, 'LINE', NULL, 2, '', '2da8caa967c5d80d5b3f918eb28683a1'),
(6332, 'en', 'default', '0f823bed6cba9b8049dc4eecf9eee287', 'You are using an insecure browser!', NULL, NULL, 'LINE', NULL, 2, '', '23213d53b58d52a73b5c6b791e0751da'),
(6333, 'en', 'default', 'ffa32f3198455426db78715a26184f3d', 'You are using the browser&#8217;s built-in file uploader. The WordPress uploader includes multiple file selection and drag and drop capability. <a href="#">Switch to the multi-file uploader</a>.', NULL, NULL, 'LINE', NULL, 2, '', '3762a3858f871b91761c7c17e352fc8d'),
(6334, 'en', 'default', '185a58071513be42df712fa7aa59c054', 'You are using the multi-file uploader. Problems? Try the <a href="%1$s" target="%2$s">browser uploader</a> instead.', NULL, NULL, 'LINE', NULL, 2, '', 'acee86659ffcf9ad8390cef00f6496bb'),
(6335, 'en', 'default', '313fdf221f4a041ed3d772112928e653', 'You attempted to access the "%1$s" dashboard, but you do not currently have privileges on this site. If you believe you should be able to access the "%1$s" dashboard, please contact your network administrator.', NULL, NULL, 'LINE', NULL, 2, '', '020de39e8d89520db671b4fe148e7915'),
(6336, 'en', 'default', '7f735a3de4fa14b113264f459aa23a72', 'You attempted to edit an attachment that doesn&#8217;t exist. Perhaps it was deleted?', NULL, NULL, 'LINE', NULL, 2, '', '08496ed3c471ff8f386f2bfad6e83a98'),
(6337, 'en', 'default', '63e10978f6765868b1d450b854d82fc7', 'You attempted to edit an item that isn&#8217;t an attachment. Please go back and try again.', NULL, NULL, 'LINE', NULL, 2, '', '900cefb2779b90dfcb085ea912988321'),
(6338, 'en', 'default', '1597e47943552acb2159f24763d2183b', 'You can Search for themes by keyword, author, or tag, or can get more specific and search by criteria listed in the feature filter.', NULL, NULL, 'LINE', NULL, 2, '', 'eb4979e5ab1b2ae278daadd1702a0603'),
(6339, 'en', 'default', 'a028caf874f7a36b4d04977846a44352', 'You can Upload a theme manually if you have already downloaded its ZIP archive onto your computer (make sure it is from a trusted and original source). You can also do it the old-fashioned way and copy a downloaded theme&#8217;s folder via FTP into your %s directory.', NULL, NULL, 'LINE', NULL, 2, '', '333d26c9fe96a9e26648cc1908869146'),
(6340, 'en', 'default', '5d8dccaf5152dadcf683142ff8e39d85', 'You can add links here to be displayed on your site, usually using <a href="%s">Widgets</a>. By default, links to several sites in the WordPress community are included as examples.', NULL, NULL, 'LINE', NULL, 2, '', 'b76bb7ad7b1732e2160f2f83dbe88287'),
(6341, 'en', 'default', '68219ec1373a07050daabcab0c978815', 'You can add or edit links on this screen by entering information in each of the boxes. Only the link&#8217;s web address and name (the text you want to display on your site as the link) are required fields.', NULL, NULL, 'LINE', NULL, 2, '', '6912f31d5f004663b4c8e4ad15c73082'),
(6342, 'en', 'default', '4f2592dccb64c180a81e4a042fe70670', 'You can also choose a background color by clicking the Select Color button and either typing in a legitimate HTML hex value, e.g. &#8220;#ff0000&#8221; for red, or by choosing a color using the color picker.', NULL, NULL, 'LINE', NULL, 2, '', 'b7e503868ca5ca70400ee20723cb8c47'),
(6343, 'en', 'default', '6b872e2b359ab19726c69cfc6662980b', 'You can also control the display of your content in RSS feeds, including the maximum number of posts to display and whether to show full text or a summary.', NULL, NULL, 'LINE', NULL, 2, '', 'ac7ebf12f6abef2a950abe5280a54102'),
(6344, 'en', 'default', '001ae354418426ceb96e7d035ad9de3c', 'You can also moderate the comment from this screen using the Status box, where you can also change the timestamp of the comment.', NULL, NULL, 'LINE', NULL, 2, '', '8c34b8c1eaba94f7289ab3289a5399fe'),
(6345, 'en', 'default', 'ff2945d6f4371f51df1c739ba60f27ae', 'You can assign keywords to your posts using <strong>tags</strong>. Unlike categories, tags have no hierarchy, meaning there&#8217;s no relationship from one tag to another.', NULL, NULL, 'LINE', NULL, 2, '', 'df93416910d6e5d402da0166617b2ee4'),
(6346, 'en', 'default', 'ceb79a4cdaccc9c24ea22489ba03429f', 'You can change the display of this screen using the Screen Options tab to set how many items are displayed per screen and to display/hide columns in the table.', NULL, NULL, 'LINE', NULL, 2, '', '59cf444b48715208ad794a188fdb27a3'),
(6347, 'en', 'default', 'aa6b50ebfd26013f727b5bddd1a0020a', 'You can change your password, turn on keyboard shortcuts, change the color scheme of your WordPress administration screens, and turn off the WYSIWYG (Visual) editor, among other things. You can hide the Toolbar (formerly called the Admin Bar) from the front end of your site, however it cannot be disabled on the admin screens.', NULL, NULL, 'LINE', NULL, 2, '', 'b9b4d0532a778cafb41ef6adf422c685'),
(6348, 'en', 'default', 'ed3a1364a6f32d0152a9a84b1badda00', 'You can change your profile picture on <a href="%s">Gravatar</a>.', NULL, NULL, 'LINE', NULL, 2, '', 'c6a264b0634dfa17c05df0e6129f3f73'),
(6349, 'en', 'default', '3a94690f89aea910c66bdb3b6fff1fc0', 'You can choose from the theme&#8217;s default header images, or use one of your own. You can also customize how your Site Title and Tagline are displayed.', NULL, NULL, 'LINE', NULL, 2, '', 'a9f5169f4cc9dff9335f02dc21036a92'),
(6350, 'en', 'default', '60a664b6a8f309a460918c0a26f8a6c1', 'You can choose one of your previously uploaded headers, or show a random one.', NULL, NULL, 'LINE', NULL, 2, '', 'd47efcdc40344fa4bb4ed32631d75492'),
(6351, 'en', 'default', '64569debf6bd3abbd212384c1a3d0a46', 'You can choose what&#8217;s displayed on the front page of your site. It can be posts in reverse chronological order (classic blog), or a fixed/static page. To set a static home page, you first need to create two <a href="%s">Pages</a>. One will become the front page, and the other will be where your posts are displayed.', NULL, NULL, 'LINE', NULL, 2, '', '467557c5c3be0b00bf0e489063fc1179');
INSERT INTO `wp_icl_strings` (`id`, `language`, `context`, `name`, `value`, `string_package_id`, `location`, `type`, `title`, `status`, `gettext_context`, `domain_name_context_md5`) VALUES
(6352, 'en', 'default', '0754b49868a7a0ac641085882e136d75', 'You can choose whether or not your site will be crawled by robots, ping services, and spiders. If you want those services to ignore your site, click the checkbox next to &#8220;Discourage search engines from indexing this site&#8221; and click the Save Changes button at the bottom of the screen. Note that your privacy is not complete; your site is still visible on the web.', NULL, NULL, 'LINE', NULL, 2, '', '1051f547d613d166e58ee2267ea6046e'),
(6353, 'en', 'default', '4e171722a615bf0a18d4a10f47f71464', 'You can create groups of links by using Link Categories. Link Category names must be unique and Link Categories are separate from the categories you use for posts.', NULL, NULL, 'LINE', NULL, 2, '', '4b54c6d68320d2f035fccf562e2bab97'),
(6354, 'en', 'default', 'f88f2ffbd623a95e6cdda7118b8edd35', 'You can create the %s manually and paste the following text into it.', NULL, NULL, 'LINE', NULL, 2, '', '4c21aaa114c572d58612b7c1eae4b985'),
(6355, 'en', 'default', '3f1914dd8eecd9de86a33cddc8389cbb', 'You can customize the display of this screen in a number of ways:', NULL, NULL, 'LINE', NULL, 2, '', '75b6f3ea46162ff1ee094bb0533bebb6'),
(6356, 'en', 'default', 'd6ea257fc3b6e7e133f43b11581d331a', 'You can customize the display of this screen using the Screen Options tab and/or the dropdown filters above the links table.', NULL, NULL, 'LINE', NULL, 2, '', 'e7d15f9c59ea3e6ee7f40b3c1e031594'),
(6357, 'en', 'default', 'ea5fcb3e59f97c7e70111b588d78d687', 'You can customize the look of your site without touching any of your theme&#8217;s code by using a custom background. Your background can be an image or a color.', NULL, NULL, 'LINE', NULL, 2, '', 'b39450760894637b67668aae44c27c96'),
(6358, 'en', 'default', '5aaf5b7354c090ad1a53700fdd8fe022', 'You can delete Link Categories in the Bulk Action pull-down, but that action does not delete the links within the category. Instead, it moves them to the default Link Category.', NULL, NULL, 'LINE', NULL, 2, '', 'b8541d8f687d12a898125b597c36ca87'),
(6359, 'en', 'default', '319980b189e432ee59526496b2fc7bd7', 'You can edit the image while preserving the thumbnail. For example, you may wish to have a square thumbnail that displays just a section of the image.', NULL, NULL, 'LINE', NULL, 2, '', '84a98079b9b86c1c74f975e3e854fa1b'),
(6360, 'en', 'default', '29a977dfe4c91dbbb1970d8a38a81a2a', 'You can edit the information left in a comment if needed. This is often useful when you notice that a commenter has made a typographical error.', NULL, NULL, 'LINE', NULL, 2, '', '995b8ae48206399e7a9530a8154fbbd3'),
(6361, 'en', 'default', '439e85bf8c00d0e5d97a3f08a503f1ce', 'You can export a file of your site&#8217;s content in order to import it into another installation or platform. The export file will be an XML file format called WXR. Posts, pages, comments, custom fields, categories, and tags can be included. You can choose for the WXR file to include only certain posts or pages by setting the dropdown filters to limit the export by category, author, date range by month, or publishing status.', NULL, NULL, 'LINE', NULL, 2, '', '9588f209116dc30d63fdf43bb35e6897'),
(6362, 'en', 'default', '336a3ba49f4fd88e0d9f10b69511d0df', 'You can filter the list of users by User Role using the text links above the users list to show All, Administrator, Editor, Author, Contributor, or Subscriber. The default view is to show all users. Unused User Roles are not listed.', NULL, NULL, 'LINE', NULL, 2, '', '8653fbcfa867f1c95e2d3b856e2d8b47'),
(6363, 'en', 'default', '415205a59aff75e660caf73215b1dce4', 'You can find additional themes for your site by using the Theme Browser/Installer on this screen, which will display themes from the <a href="%s">WordPress Theme Directory</a>. These themes are designed and developed by third parties, are available free of charge, and are compatible with the license WordPress uses.', NULL, NULL, 'LINE', NULL, 2, '', 'b7b2dd7c837caf0e94c8104888321a73'),
(6364, 'en', 'default', '67e7c46d3d5c2108c0e99ceea6441dee', 'You can hide/display columns based on your needs and decide how many users to list per screen using the Screen Options tab.', NULL, NULL, 'LINE', NULL, 2, '', '48e4f7cb4f7122f5b317b5c99b0da496'),
(6365, 'en', 'default', 'b9ee24b2ed698a7774a132f859a7440b', 'You can log out of other devices, such as your phone or a public computer, by clicking the Log Out Everywhere Else button.', NULL, NULL, 'LINE', NULL, 2, '', '1b241a88804ad03a64423db084510037'),
(6366, 'en', 'default', '74ec803b504414d42126006142f54490', 'You can manage comments made on your site similar to the way you manage posts and other content. This screen is customizable in the same ways as other management screens, and you can act on comments using the on-hover action links or the Bulk Actions.', NULL, NULL, 'LINE', NULL, 2, '', '054e0c1e1a79152711e5105dee4b047f'),
(6367, 'en', 'default', '82169939daa05d25d68df1a2d649d306', 'You can narrow the list by file type/status or by date using the dropdown menus above the media table.', NULL, NULL, 'LINE', NULL, 2, '', 'db58d14d2240eb7c402d9fbd316f6e77'),
(6368, 'en', 'default', 'ae310675793fc7bfa950b8760f79a4f1', 'You can now manage and live-preview Custom Backgrounds in the <a href="%1$s">Customizer</a>.', NULL, NULL, 'LINE', NULL, 2, '', '7cb915125bad49f15eac14767efbd6ce'),
(6369, 'en', 'default', '5c71e77cf2c930baea8c287753a229c2', 'You can now manage and live-preview Custom Header in the <a href="%1$s">Customizer</a>.', NULL, NULL, 'LINE', NULL, 2, '', 'a7882a3b8d2131be6d89949cc08d58da'),
(6370, 'en', 'default', '82deca0fb59319be08a217a38bcd7d23', 'You can proportionally scale the original image. For best results, scaling should be done before you crop, flip, or rotate. Images can only be scaled down, not up.', NULL, NULL, 'LINE', NULL, 2, '', 'f8773edde68d4c1d2cea0cef4a3554d0'),
(6371, 'en', 'default', '3466e4fdb669b751b77cbac98d3b8a72', 'You can select an image to be shown at the top of your site by uploading from your computer or choosing from your media library. After selecting an image you will be able to crop it.', NULL, NULL, 'LINE', NULL, 2, '', '970337f1d4150e8b177c927ebf99c80b'),
(6372, 'en', 'default', 'ecf5914ee8ec7cfa4e0744d83a3cfb09', 'You can select the language you wish to use while using the WordPress administration screen without affecting the language site visitors see.', NULL, NULL, 'LINE', NULL, 2, '', 'fd5735268d8e29b5fb70169e194cfcba'),
(6373, 'en', 'default', 'bc6ead191133be47290a0774386abc1e', 'You can set a custom image header for your site. Simply upload the image and crop it, and the new header will go live immediately. Alternatively, you can use an image that has already been uploaded to your Media Library by clicking the &#8220;Choose Image&#8221; button.', NULL, NULL, 'LINE', NULL, 2, '', '811547aebb06fa74b5ec521c1f410710'),
(6374, 'en', 'default', 'ca07cec969f8aa527834073e21e414b6', 'You can set maximum sizes for images inserted into your written content; you can also insert an image as Full Size.', NULL, NULL, 'LINE', NULL, 2, '', 'a6516e825fa24e1b092cc7b8c1eabd17'),
(6375, 'en', 'default', '0587e968316b6f9279f46763cd67c781', 'You can still use your site but any subdomain you create may not be accessible. If you know your DNS is correct, ignore this message.', NULL, NULL, 'LINE', NULL, 2, '', '91538971a3045305eef53a3d987e6a97'),
(6376, 'en', 'default', '562a2da5ecf13083d751ce8dd9905d10', 'You can submit content in several different ways; this screen holds the settings for all of them. The top section controls the editor within the dashboard, while the rest control external publishing methods. For more information on any of these methods, use the documentation links.', NULL, NULL, 'LINE', NULL, 2, '', 'f86ab1682d11f3bf7e8923776b1c375c'),
(6377, 'en', 'default', '09df6ae117e587dd779421384cd15bff', 'You can upload media files here without creating a post first. This allows you to upload files to use with posts and pages later and/or to get a web link for a particular file that you can share. There are three options for uploading files:', NULL, NULL, 'LINE', NULL, 2, '', '5f22753ed743a0fce788ebbb26a44f91'),
(6378, 'en', 'default', '4d26943fa96797a4136ef47c5084b772', 'You can use categories to define sections of your site and group related posts. The default category is &#8220;Uncategorized&#8221; until you change it in your <a href="%s">writing settings</a>.', NULL, NULL, 'LINE', NULL, 2, '', '58e0f4bd0db3f201181a24e22518af6b'),
(6379, 'en', 'default', 'c5466e9372fdad7ba27494dd68c9fcb2', 'You can use one of these cool headers or show a random one on each page.', NULL, NULL, 'LINE', NULL, 2, '', '8f8ebb3ba270539ec6f49120ea9c16cc'),
(6380, 'en', 'default', 'c0ed5e3f5e633991901390dd59336cc6', 'You can use the editor to make changes to any of your plugins&#8217; individual PHP files. Be aware that if you make changes, plugins updates will overwrite your customizations.', NULL, NULL, 'LINE', NULL, 2, '', 'f835ba39685bf5ad176052a11180bb18'),
(6381, 'en', 'default', '809cd0ac3a99a17e88b7af070dcb1d97', 'You can view all posts made by a user by clicking on the number under the Posts column.', NULL, NULL, 'LINE', NULL, 2, '', '057d0d295c4e9c03bbba615601760927'),
(6382, 'en', 'default', '0ba7d2aceb1e3523389309177b68f301', 'You can&#8217;t delete the current user.', NULL, NULL, 'LINE', NULL, 2, '', 'e6666e309ecbdfe0130e85f382aead42'),
(6383, 'en', 'default', '8f202ef980323b961b4772629bedd1e1', 'You can&#8217;t edit this attachment because it is in the Trash. Please move it out of the Trash and try again.', NULL, NULL, 'LINE', NULL, 2, '', 'cfc058edbc65f0e4e05713f17e853065'),
(6384, 'en', 'default', '2e55a67bc4775ccbeb957f8ac48542d6', 'You can&#8217;t edit this item because it is in the Trash. Please restore it and try again.', NULL, NULL, 'LINE', NULL, 2, '', '2fd0c2ec1e5e32349b9c52297dcaaafc'),
(6385, 'en', 'default', '418bfc8c609d4e4688d50eaf15e0f2b9', 'You can&#8217;t give users that role.', NULL, NULL, 'LINE', NULL, 2, '', '0d53ded15e4cd459c6df27c37de85d51'),
(6386, 'en', 'default', '1ed040d2bd69736871c3b386dc066d07', 'You can&#8217;t remove users.', NULL, NULL, 'LINE', NULL, 2, '', '9a340ce96923e19e7186b326b75bb879'),
(6387, 'en', 'default', '42760c290b5dd70549bd26bd19df254b', 'You can\'t remove the current user.', NULL, NULL, 'LINE', NULL, 2, '', '865abc726875f0e6044ef01e8bfd28ad'),
(6388, 'en', 'default', '3f334c7e52a82632c6c079af0e2fd865', 'You cannot delete a plugin while it is active on the main site.', NULL, NULL, 'LINE', NULL, 2, '', '17cd18c5c77c3a46acddf5f4a8396581'),
(6389, 'en', 'default', 'a59e9803ef8f9c934b599c7bc82ebb85', 'You cannot delete a theme while it has an active child theme.', NULL, NULL, 'LINE', NULL, 2, '', 'a9819b45ff4f1ccced946d75a412f263'),
(6390, 'en', 'default', '00bedaae09117c56e382ed2dd136d06f', 'You cannot install because <a href="https://codex.wordpress.org/Version_%1$s">WordPress %1$s</a> requires MySQL version %2$s or higher. You are running version %3$s.', NULL, NULL, 'LINE', NULL, 2, '', 'b0b04b113a6fb24bfe6eb9ad329c8560'),
(6391, 'en', 'default', '400104f5aa7e3a979173f8aac3594177', 'You cannot install because <a href="https://codex.wordpress.org/Version_%1$s">WordPress %1$s</a> requires PHP version %2$s or higher and MySQL version %3$s or higher. You are running PHP version %4$s and MySQL version %5$s.', NULL, NULL, 'LINE', NULL, 2, '', 'aa016e5963647ce24f572658d864d064'),
(6392, 'en', 'default', '5b54d9d7eb99d25fb932270b53ddfae2', 'You cannot install because <a href="https://codex.wordpress.org/Version_%1$s">WordPress %1$s</a> requires PHP version %2$s or higher. You are running version %3$s.', NULL, NULL, 'LINE', NULL, 2, '', 'af063145ee7151d780cb2f65472880fa'),
(6393, 'en', 'default', '89511b0ab3c0336f7966eb386c55310e', 'You cannot move this item to the Trash. %s is currently editing.', NULL, NULL, 'LINE', NULL, 2, '', 'c6115809e477528aa86010d11ad152f0'),
(6394, 'en', 'default', 'a16e62a922bf489c2cad4c0a015c9e00', 'You cannot update because <a href="https://codex.wordpress.org/Version_%1$s">WordPress %1$s</a> requires MySQL version %2$s or higher. You are running version %3$s.', NULL, NULL, 'LINE', NULL, 2, '', '6bffd30f38521ab4236928dd5ca4f94b'),
(6395, 'en', 'default', 'af16ea89802a72d2349dd2cf9f154203', 'You cannot update because <a href="https://codex.wordpress.org/Version_%1$s">WordPress %1$s</a> requires PHP version %2$s or higher and MySQL version %3$s or higher. You are running PHP version %4$s and MySQL version %5$s.', NULL, NULL, 'LINE', NULL, 2, '', 'a49b9412aa1ef33a1cf20ae3d5d93282'),
(6396, 'en', 'default', '9d3950bfbf1f532636941e6e7472d526', 'You cannot update because <a href="https://codex.wordpress.org/Version_%1$s">WordPress %1$s</a> requires PHP version %2$s or higher. You are running version %3$s.', NULL, NULL, 'LINE', NULL, 2, '', 'fa47aa31c394af7be58786ff71d047c1'),
(6397, 'en', 'default', 'a34c26b14e4cc0bbbc7539acf0fd8c13', 'You did not enter a category name.', NULL, NULL, 'LINE', NULL, 2, '', 'bf8173990f84489006dd9def7d3eaa40'),
(6398, 'en', 'default', 'e15fb7addf8eb9749136275482f556bf', 'You have %1$s and %2$s.', NULL, NULL, 'LINE', NULL, 2, '', 'b2418cd4bfe76e0ffedcf84970b8d4d7'),
(6399, 'en', 'default', '7167162991d8800ebce7e1bd624574d9', 'You have access to the source code, the freedom to study how the program works, and the freedom to change it to make it do what you wish.', NULL, NULL, 'LINE', NULL, 2, '', '67c789df58149c9af13602cb20315029'),
(6400, 'en', 'default', '231c8ae8e02bd4937e66b0fa0b9588ad', 'You have chosen to delete the following users from all networks and sites.', NULL, NULL, 'LINE', NULL, 2, '', '743cf80abb6d03cae3640471aabe3491'),
